// Get the total volume for top 10 customers by revenue:

$('data')
  .filter(
    $('customer').in(
      $('data').split('$customer', 'Customer')
        .apply('Revenue', '$data.sum($revenue)')
        .sort('$Revenue', 'descending')
        .limit(10)
        .collect('$Customer')
    )
  )
  .sum('$volume');
















