ply()
  .apply('auctions', $('auctions').filter('$country == "UK"'))
  .apply('Count', '$auctions.count()')
  .apply('Revenue', '$auctions.sum($revenue)')
  .apply('Bidder',
    $('auctions').split('$bidder', 'BidderName')
      .apply('Revenue', '$auctions.sum($revenue)')
      .apply('PercentOfTotalRevenue', '$Revenue / $^Revenue * 100')
      .limit(100)
      .apply('BidCategories',
        $('auctions').split('$category', 'Category')
          .apply('Products', '$auctions.countDistinct($product_id)')
          .sort('$Products', 'descending')
          .limit(5)
      )
  )
